import _ from 'lodash';

class StubAPI {
    constructor() {
        this.contacts = [
            {
            'dart_player': 'Contact 1',
            'address': '123 Test St',
            'phone_number': '132-3212'
            },        
            {
            'dart_player': 'Contact 2',
            'address': '23 Main St',
            'phone_number': '934-4329'
            }, 
            {
            'dart_player': 'Contact 3',
            'address': '4 Lower St',
            'phone_number': '432-5832'
            },
            {
            'dart_player': 'Contact 4',
            'address': '49 Upper Street',
            'phone_number': '934-4290'
            },
            {
            'dart_player': 'Contact 5',
            'address': '4 High Street',
            'phone_number': '933-3390'
            }
        ] ; 
    }

    delete(k) {
        let elements = _.remove(this.contacts, 
            (contact) => contact.phone_number === k
        );
        return elements; 
    }
    getAll() {
        return this.contacts ;
    }

    add(n,a,p) {
        let len = this.contacts.length ;
        let newLen = this.contacts.push({
          dart_player: n, address : a, phone_number: p }) ;
        return newLen > len ;
    }

    update(key,n,a,p) {
        let index = _.findIndex(this.contacts, 
            (contact) => contact.phone_number === key
        );      
        if (index !== -1) {
            this.contacts.splice(index, 1, 
                {dart_player: n, address: a, phone_number: p});
            return true ;
        }
        return false ;
    }
}

export default (new StubAPI() );
